  str.split('').reduce((reversed, a) => a + reversed, '');

// 提供一個額外array放結果
const reverse2 = (str = '') => {
  let reversedStr = '';
  for (const s of str) {
    reversedStr = s + reversedStr;
  }
  return reversedStr;
};

// 用現有js提供的reverse 函數
const reverse1 = (str = '') =>
  str
    .split('')
    .reverse()
    .join('');

//(me) 不需要額外空間
const reverse4 = (str = '') => {
  if (str.length === 0) return str;

  let source = str.split('');
  let back = 0;
  let front = source.length - 1;

  while (back <= front) {
    if (back === front) {
      break;
    }

    //swap
    let temp = source[front];
    source[front] = source[back];
    source[back] = temp;

    back += 1;
    front -= 1;
  }
  return source.reduce((sum, a) => sum + a, '');
};

module.exports = reverse;

```

